mapper.select:
SELECT

FROM t_sale_product a

WHERE a.id = #{id}

SELECT

FROM t_sale_product a


    a.del_flag = #{DEL_FLAG_NORMAL}

        and a.siteId like concat('%', #{siteid}, '%')


        and a.productId like concat('%', #{productid}, '%')


        AND a.price = #{price}




        ORDER BY ${page.orderByColumn}

SELECT
    a.id
FROM t_sale_product a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND id != #{notEqualId}

mapper.update:
UPDATE t_sale_product SET
    del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
