mapper.select:
SELECT

FROM t_shipping_message a

WHERE a.id = #{id}

SELECT

FROM t_shipping_message a


    a.del_flag = #{DEL_FLAG_NORMAL}

        and a.district like concat('%', #{district}, '%')


        and a.address like concat('%', #{address}, '%')


        and a.userId like concat('%', #{userid}, '%')


        AND a.phone = #{phone}


        and a.consignee like concat('%', #{consignee}, '%')


        and u.consignee like concat('%', #{userName}, '%')




        ORDER BY ${page.orderByColumn}

SELECT
    a.id
FROM t_shipping_message a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND id != #{notEqualId}

mapper.update:
UPDATE t_shipping_message SET
    del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
